"use strict";

document.addEventListener('DOMContentLoaded', function () {
  //инициализация слик-слайдера на блок отзывов
  $('.feedbacks__content').slick({
    slidesToShow: 2,
    slidesToScroll: 1,
    responsive: [{
      breakpoint: 1024,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 1,
        infinite: true,
        dots: true
      }
    }, {
      breakpoint: 768,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2
      }
    }, {
      breakpoint: 576,
      settings: {
        adaptiveHeight: true,
        slidesToShow: 1,
        slidesToScroll: 1
      }
    } // You can unslick at a given breakpoint now by adding:
    // settings: "unslick"
    // instead of a settings object
    ]
  }); //открытие и закрытие мобильного меню

  var mobileMenu = document.querySelector('.tgl-menu');
  var menuButton = document.querySelector('.header__menu-button');
  var menuArrow = document.querySelector('.tgl-menu__arrow');
  menuButton.onclick = toggleMenu;
  menuArrow.onclick = toggleMenu;

  function toggleMenu() {
    mobileMenu.classList.toggle('tgl-menu_active');
    blockBody();
  }

  function blockBody() {
    mobileMenu.classList.contains('tgl-menu_active') ? document.body.style.overflow = 'hidden' : document.body.style.overflow = '';
  } //перемотка до блока по нажатию ссылки


  var currentPageLinks = document.querySelectorAll('.currentPageLink');
  currentPageLinks.forEach(function (currentPageLink) {
    currentPageLink.addEventListener('click', function (e) {
      e.preventDefault();
      var target = document.querySelector("".concat(this.getAttribute('href')));

      if (getComputedStyle(mobileMenu).display != 'none') {
        toggleMenu();
        setTimeout(function () {
          target.scrollIntoView({
            behavior: "smooth"
          });
        }, 1000);
      } else {
        setTimeout(function () {
          target.scrollIntoView({
            behavior: "smooth"
          });
        }, 300);
      }
    });
  });
});
